          <div>
            <Label htmlFor="bankName" className="text-sm font-medium text-foreground mb-2 block">
              Banka AdÄ±
            </Label>
            <Input
              id="bankName"
              placeholder="Ã–rn: YapÄ± Kredi, Garanti, Ä°ÅŸ BankasÄ±"
