import { Request, Response, NextFunction } from 'express';
import { UserRoleType, PermissionType, hasPermission, hasAnyPermission } from '@shared/schema';

// Extend Request type to include user info
export interface AuthenticatedRequest extends Request {
  user?: {
    id: string;
    email: string;
    username: string;
    role: UserRoleType;
  };
}

// Authentication middleware - ensures user is logged in and active (authoritative check)
export const requireAuth = async (req: AuthenticatedRequest, res: Response, next: NextFunction) => {
  if (!req.session?.user) {
    return res.status(401).json({ 
      error: 'Oturum aÃ§manÄ±z gerekiyor',
      code: 'AUTH_REQUIRED' 
    });
  }

  try {
    // Load fresh user data from authoritative storage
    const { storage } = await import('../storage');
    const currentUser = await storage.getUser(req.session.user.id);

    if (!currentUser) {
      // User no longer exists - destroy session
      req.session.destroy((err) => {});
      return res.status(401).json({
        error: 'KullanÄ±cÄ± hesabÄ± bulunamadÄ±',
        code: 'USER_NOT_FOUND'
      });
    }

    // Check if user account is active (authoritative check)
    if (!currentUser.isActive) {
      req.session.destroy((err) => {});
      return res.status(403).json({
        error: 'HesabÄ±nÄ±z pasif durumda. LÃ¼tfen yÃ¶netici ile iletiÅŸime geÃ§in',
        code: 'ACCOUNT_INACTIVE'
      });
    }

    // Update session with fresh data and attach to request
    req.session.user = {
      id: currentUser.id,
      email: currentUser.email,
      username: currentUser.username,
      role: currentUser.role,
      isActive: currentUser.isActive
    };
    req.user = req.session.user as any;
    next();
  } catch (error) {
    console.error('Auth middleware error:', error);
    return res.status(500).json({
      error: 'Kimlik doÄŸrulama hatasÄ±',
      code: 'AUTH_ERROR'
    });
  }
};

// Role-based authorization middleware
export const requireRole = (...allowedRoles: UserRoleType[]) => {
  return (req: AuthenticatedRequest, res: Response, next: NextFunction) => {
    if (!req.user) {
      return res.status(401).json({ 
        error: 'Oturum aÃ§manÄ±z gerekiyor',
        code: 'AUTH_REQUIRED' 
      });
    }

    if (!allowedRoles.includes(req.user.role)) {
      return res.status(403).json({ 
        error: 'Bu iÅŸlem iÃ§in yetkiniz bulunmuyor',
        code: 'INSUFFICIENT_ROLE',
        requiredRoles: allowedRoles,
        userRole: req.user.role
      });
    }

    next();
  };
};

// Permission-based authorization middleware
export const requirePermission = (...requiredPermissions: PermissionType[]) => {
  return (req: AuthenticatedRequest, res: Response, next: NextFunction) => {
    if (!req.user) {
      return res.status(401).json({ 
        error: 'Oturum aÃ§manÄ±z gerekiyor',
        code: 'AUTH_REQUIRED' 
      });
    }

    const userHasPermission = hasAnyPermission(req.user.role, requiredPermissions);

    if (!userHasPermission) {
      return res.status(403).json({ 
        error: 'Bu iÅŸlem iÃ§in yetkiniz bulunmuyor',
        code: 'INSUFFICIENT_PERMISSION',
        requiredPermissions,
        userRole: req.user.role
      });
    }

    next();
  };
};

// Check specific permission without blocking request
export const checkPermission = (req: AuthenticatedRequest, permission: PermissionType): boolean => {
  if (!req.user) return false;
  return hasPermission(req.user.role, permission);
};

// Account type access middleware
export const requireAccountTypeAccess = (accountType: 'personal' | 'company') => {
  return (req: AuthenticatedRequest, res: Response, next: NextFunction) => {
    if (!req.user) {
      return res.status(401).json({ 
        error: 'Oturum aÃ§manÄ±z gerekiyor',
        code: 'AUTH_REQUIRED' 
      });
    }

    // Admin can access everything
    if (req.user.role === 'admin') {
      return next();
    }

    // Company users can access both
    if (req.user.role === 'company_user') {
      return next();
    }

    // Personal users can only access personal accounts
    if (req.user.role === 'personal_user' && accountType === 'personal') {
      return next();
    }

    return res.status(403).json({ 
      error: `${accountType === 'company' ? 'Åžirket' : 'KiÅŸisel'} hesaplarÄ±na eriÅŸim yetkiniz bulunmuyor`,
      code: 'ACCOUNT_TYPE_ACCESS_DENIED',
      accountType,
      userRole: req.user.role
    });
  };
};

// Optional auth middleware - attaches user if logged in but doesn't require it
export const optionalAuth = (req: AuthenticatedRequest, res: Response, next: NextFunction) => {
  if (req.session?.user) {
    req.user = req.session.user as any;
  }
  next();
};

// Admin only middleware (shorthand)
export const requireAdmin = requireRole('admin');

// Log access attempts for security audit
export const logAccess = (action: string) => {
  return (req: AuthenticatedRequest, res: Response, next: NextFunction) => {
    console.log(`ðŸ” [AUTH] ${action} - User: ${req.user?.username || 'anonymous'} (${req.user?.role || 'no-role'}) - IP: ${req.ip}`);
    next();
  };
};

client/src/hooks/useAuth.tsx.content:
import { createContext, useContext, useEffect, useState } from 'react';
import { useQuery, useMutation } from '@tanstack/react-query';
import { useLocation } from 'wouter';
import { UserRoleType, hasPermission, PermissionType } from '@shared/schema';
import { apiRequest } from '@/lib/queryClient';

interface User {
  id: string;
  email: string;
  username: string;
  role: UserRoleType;
}

interface AuthContextType {
  user: User | null;
  isLoading: boolean;
  isAuthenticated: boolean;
  hasPermission: (permission: PermissionType) => boolean;
  hasRole: (role: UserRoleType) => boolean;
  canAccessRoute: (route: string) => boolean;
  logout: () => void;
}

const AuthContext = createContext<AuthContextType | undefined>(undefined);

export function AuthProvider({ children }: { children: React.ReactNode }) {
  const [, setLocation] = useLocation();

  // Query current user
  const { data: userResponse, isLoading, error } = useQuery({
    queryKey: ['/api/auth/me'],
    retry: false, // Don't retry on 401
    staleTime: 5 * 60 * 1000, // 5 minutes
  });

  const user = (userResponse as any)?.user || null;
  const isAuthenticated = !!user && !error;

  // Logout mutation
  const logoutMutation = useMutation({
    mutationFn: () => apiRequest('POST', '/api/auth/logout'),
    onSuccess: () => {
      // Redirect to login after logout
      setLocation('/login');
    },
  });

  const logout = () => {
    logoutMutation.mutate();
  };

  // Permission check helper
  const checkPermission = (permission: PermissionType): boolean => {
    if (!user) return false;
    return hasPermission(user.role, permission);
  };

  // Role check helper
  const checkRole = (role: UserRoleType): boolean => {
    if (!user) return false;
    return user.role === role;
  };

  // Route access control based on user role
  const canAccessRoute = (route: string): boolean => {
    if (!user) return false;

    // Route-role mapping
    const routePermissions: Record<string, UserRoleType[]> = {
      '/': ['admin', 'company_user', 'personal_user'],
      '/analytics': ['admin', 'company_user', 'personal_user'],
      '/company': ['admin', 'company_user'], // Personal users can't access company routes
      '/personal': ['admin', 'company_user', 'personal_user'],
      '/transfers': ['admin', 'company_user', 'personal_user'],
      '/fixed-expenses': ['admin', 'company_user', 'personal_user'],
      '/credit-cards': ['admin', 'company_user', 'personal_user'],
      '/reports': ['admin', 'company_user', 'personal_user'],
      '/settings': ['admin', 'company_user', 'personal_user'],
      '/admin': ['admin'], // Admin-only route
    };

    const allowedRoles = routePermissions[route];
    if (!allowedRoles) return true; // Default allow if route not specified

    return allowedRoles.includes(user.role);
  };

  return (
    <AuthContext.Provider
      value={{
        user,
        isLoading,
        isAuthenticated,
        hasPermission: checkPermission,
        hasRole: checkRole,
        canAccessRoute,
        logout,
      }}
    >
      {children}
    </AuthContext.Provider>
  );
}

export function useAuth() {
  const context = useContext(AuthContext);
  if (context === undefined) {
    throw new Error('useAuth must be used within an AuthProvider');
  }
  return context;
}

// Auth guard component for protecting routes
export function AuthGuard({ children }: { children: React.ReactNode }) {
  const { isAuthenticated, isLoading } = useAuth();
  const [, setLocation] = useLocation();

  useEffect(() => {
    if (!isLoading && !isAuthenticated) {
      setLocation('/login');
    }
  }, [isAuthenticated, isLoading, setLocation]);

  if (isLoading) {
    return (
      <div className="flex items-center justify-center min-h-screen">
        <div className="animate-spin rounded-full h-32 w-32 border-b-2 border-primary"></div>
      </div>
    );
  }

  if (!isAuthenticated) {
    return null; // Will redirect to login
  }

  return <>{children}</>;
}

// Role guard component for role-based access
export function RoleGuard({ 
  allowedRoles, 
  children,
  fallback 
}: { 
  allowedRoles: UserRoleType[];
  children: React.ReactNode;
  fallback?: React.ReactNode;
}) {
  const { user } = useAuth();

  if (!user || !allowedRoles.includes(user.role)) {
    if (fallback) {
      return <>{fallback}</>;
    }

    return (
      <div className="flex items-center justify-center min-h-[400px]">
        <div className="text-center">
