import { Card, CardContent } from "@/components/ui/card";
import { TrendingUp, TrendingDown, DollarSign } from "lucide-react";

interface KPIBarProps {
  totalCash: number;
  totalDebt: number;
  totalBalance: number;
  formatCurrency: (amount: string) => string;
  isLoading?: boolean;
}

export default function KPIBar({ 
