import { queryClient, apiRequest } from "@/lib/queryClient";

interface JsonStatus {
  exists: boolean;
  isValid: boolean;
  transactionCount?: number;
  lastExport?: string;
}

export function TransactionJsonManager() {
  const [overwriteExisting, setOverwriteExisting] = useState(false);
