  // JSON'a dÄ±ÅŸa aktarma
  const exportMutation = useMutation({
    mutationFn: async () => {
      const response = await apiRequest('POST', '/api/transactions/export-json');
      return response.json();
    },
    onSuccess: (data) => {
      refetchStatus();
      toast({
        title: "DÄ±ÅŸa aktarma baÅŸarÄ±lÄ±",
        description: data.message,
      });
    },
    onError: () => {
      toast({
        title: "DÄ±ÅŸa aktarma hatasÄ±",
        description: "Ä°ÅŸlemler JSON'a aktarÄ±lÄ±rken bir hata oluÅŸtu",
        variant: "destructive",
      });
    }
  });

  // JSON'dan iÃ§e aktarma  
  const importMutation = useMutation({
    mutationFn: async (overwrite: boolean) => {
      const response = await apiRequest('POST', '/api/transactions/import-json', {
        overwriteExisting: overwrite
      });
      return response.json();
    },
    onSuccess: (data) => {
      queryClient.invalidateQueries({ queryKey: ['/api/transactions'] });
      queryClient.invalidateQueries({ queryKey: ['/api/dashboard'] });
      refetchStatus();
      toast({
        title: "Ä°Ã§e aktarma baÅŸarÄ±lÄ±",
        description: data.message,
      });
    },
    onError: () => {
      toast({
        title: "Ä°Ã§e aktarma hatasÄ±",
        description: "JSON'dan iÅŸlemler iÃ§e aktarÄ±lÄ±rken bir hata oluÅŸtu",
