      });
    }
  });

  // Kategori analizi dÄ±ÅŸa aktarma
  const exportCategoryAnalysisMutation = useMutation({
    mutationFn: async () => {
      const response = await apiRequest('POST', '/api/transactions/export-category-analysis');
      return response.json();
    },
